exports.getEngineRegex = async function(engine) {
  switch (engine) {
    case 'dollar':
      return /\${([^\}]+)}/
    case 'hash':
      return /\#{([^\}]+)}/;
    case 'nextgen':
      return /<\+([^\>]+)>/;
    case 'dollarORnextgen':
      return /\${([^\}]+)}|<\+([^\>]+)>/
    case 'hashORnextgen':
      return /\#{([^\}]+)}|<\+([^\>]+)>/
    default:
  }
  // default to dollarORnextgen
  return /\${([^\}]+)}|<\+([^\>]+)>/
}
